import React,{useState} from 'react';
import { Button, StyleSheet, Text, View, TextInput } from 'react-native';

export default function App() {
  const [name, setName] = useState('Shaun');
  const [age, setAge] = useState(40);


  return (
    <View style={styles.container}>
        <Text>Enter your name here </Text>
        <TextInput style={styles.textinputter} placeholder="eg. donna" onChangeText={(val) => setName(val)}/>

        <Text>Enter your age here </Text>
        <TextInput style={styles.textinputter} placeholder="eg. 30" onChangeText={(val) => setName(val)}/>

        <Text> Name: {name} Age: {age} </Text>

    </View>
  ); 
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  textinputter: {
    borderWidth:2,
    borderColor: '#777',
    padding: 8,
    margin: 10,

  },
  input:{
    color: 'grey',
    fontSize:20
  },

});
